const schedule = flow.get("schedule");
let start = env.get("start");
let stop = env.get("stop");
let days = env.get("days");
if(schedule !== undefined){
    start = schedule.start;
    stop = schedule.stop;
    days = schedule.days;
}
const time = new Date();
let day = time.getDay();
if(day === 0) day = 7;
let hour = String(time.getHours());
let minute = String(time.getMinutes());
if(hour.length == 1) hour = "0" + hour;
if(minute.length == 1) minute = "0" + minute;
const hmtime = hour + ":" + minute;
if(days.includes(day)){
    if(start == stop){
        return [msg, null];
    } else if(start > stop){
        if(hmtime >= start || hmtime < stop){
            return [msg, null];
        } else {
            return [null, msg];
        }
    } else if(hmtime >= start && hmtime < stop){
        return [msg, null];
    } else {
        return [null, msg];
    }
} else {
    return null;
}

